let mongoose = require('mongoose');
let Schema = mongoose.Schema;

// Model to store Rounded Amounts for Stripe to Access for creating ACH Charges.
let roundedTransSchema = new Schema({

    //deposit id auto generated by mongo
    userID: {
        type: String,
        required: true,
        trim: true
    },
    // Account_id associated to the account the transaction came from.
    account_id: {
        type: String,
        required: true
    },
    // name of the transaction that is being rounded up.
    transactionName: {
        type: String,
        required: true
    },
    // the original transaction amount.
    originalAmount: {
        type: Number,
        required: true
    },
    // The currency code for reference.
    currencyCode: {
        type: String,
        required: true
    },
    // Category of transaction.
    // For future use for User Savings Preferences & Settings.
    category: {
        type: Array,
        required: true
    },
    // amount rounded up from transaction to be accessed from Stripe
    // to create the initial ACH Request.
    roundedAmount: {
        type: Number,
        required: true
    },
    transaction_id: {
        type: String,
        required: true,
        unique: true
    },
    transactionDate: {
        type: Date,
        required: true
    }
});

let RoundedTrans = mongoose.model("RoundedTrans", roundedTransSchema)

module.exports = RoundedTrans;